task = "{\n    \"name\": \"Fallout and Propaganda\",\n    \"type\": \"Dynamic Quest\",\n    \"complete\": false,\n    \"narrative\": {\n        \"1\": \"The protagonist navigates the charged atmosphere in Zyra, where illusion projections and fiery speeches shape public perception.\",\n        \"2\": \"In Drajh, the protagonist witnesses the theocratic response to the explosion, with calls for divine retribution and military enforcement.\",\n        \"3\": \"The protagonist must choose a path that influences the unfolding events, balancing between propaganda, investigation, and neutrality.\"\n    },\n    \"answers\": {\n        \"1\": [\n            \"Spread Propaganda - 'Let the truth shine. The people must see Zyra\u2019s resolve.'\",\n            \"Question Narrative - 'Are we so certain of Drajh\u2019s guilt? We must examine the evidence.'\",\n            \"Remain Neutral - 'We need stability, not accusations. Focus on defending the city.'\"\n        ],\n        \"2\": [\n            \"Fan the Flames - 'Faith guides us. We must prepare for holy war!'\",\n            \"Urge Caution - 'Revenge blinds us. We need strategy, not fury.'\",\n            \"Deflect Focus - 'We must protect our people first. Fortify the borders.'\"\n        ],\n        \"3\": [\n            \"Spread Propaganda (Zyra/Drajh) - Gain influence within ruling factions and unlock access to state resources.\",\n            \"Question the Narrative - Build alliances with scholars or dissenters, opening alternate investigation paths.\",\n            \"Remain Neutral - Maintain flexibility and avoid extreme positions, keeping diplomatic options open.\"\n        ]\n    },\n    \"scripts\": {\n        \"1\": \"Quest.handleZyraChoices\",\n        \"2\": \"Quest.handleDrajhChoices\",\n        \"3\": \"Quest.handleBranchingPaths\"\n    },\n    \"data\": {\n        \"environment\": \"Zyra and Drajh settings with distinct political and religious tensions.\",\n        \"key_npcs\": \"Chancellor Aeris, High Inquisitor Kaelith, Steward Caervas\",\n        \"protagonist_role\": \"Branch A1, B1, C1 options for different paths\",\n        \"immediate_consequences\": \"Crowds disperse or escalate to riots; artifact fragments glow; rumors of demons.\"\n    }\n}"
