task = "{\n    \"name\": \"The Sunken Hold\",\n    \"type\": \"Dynamic Quest\",\n    \"complete\": false,\n    \"narrative\": {\n        1: \"The group enters The Sunken Hold, a half-submerged ruin filled with arcane energy and hidden dangers.\",\n        2: \"The protagonist must choose between escaping quickly, protecting the group, or exploring further.\",\n        3: \"A climactic event occurs as the vault is unsealed, awakening a bound demon.\"\n    },\n    \"answers\": {\n        1: [\n            \"Follow Myrra - Escape Quickly\",\n            \"Support Kelgar - Protect the Group\",\n            \"Back Ashki - Explore Further\"\n        ],\n        2: [\n            \"Protect Ashki\",\n            \"Seal the Vault\",\n            \"Use Relics\"\n        ]\n    },\n    \"scripts\": {\n        1: \"Quest.navigate_traps\",\n        2: \"Quest.defend_refugees\",\n        3: \"Quest.investigate_rift\"\n    },\n    \"data\": {\n        \"environment\": \"The Sunken Hold emerges from the mist, half-submerged in black water with moss-covered stone towers.\",\n        \"key_npcs\": [\n            \"Myrra (Smuggler)\",\n            \"Kelgar (Cleric)\",\n            \"Ashki (Sorcerer)\"\n        ],\n        \"protagonist_role\": [\n            \"Escape Route\",\n            \"Defend Refugees\",\n            \"Investigate Rift\"\n        ],\n        \"branching_paths\": [\n            \"Escape Quickly\",\n            \"Protect the Group\",\n            \"Explore Further\"\n        ],\n        \"climactic_event\": \"Vault Unsealing\"\n    }\n}"
